const formation = [
     
     {
          "id":"web",
          "nom":"web development",
          "duree":"3 ans",
          "date": "19/08/2021",
          "img":"src"
     },
     {
          "id":"devops",
          "nom":"DevOps",
          "duree":"2 ans",
          "date": "19/08/2021",
          "img":"src"
     },
     
     {
          "id":"cybersecurite",
          "nom":"Cybersecurite",
          "duree":"1.5 ans",
          "date": "19/12/2021"
     }
     

]


function FormationPage(){
	
	return (
		<div className="page formation">
               <h1>Nos formations</h1>
               <div className="grided formationGrid">
                    {
                         formation.map((formationItem)=>{
                              return(
                                   <div className="formationChild grided">
                                        <div className="formationImg">

                                        </div>
                                        <div  className="formationInfo">
                                             <h2>{formationItem.nom}</h2>
                                             <ul className="formationDetails">
                                                  <li>Duree: {formationItem.duree}</li>
                                                  <li key={formationItem.date}>date de debut: {formationItem.date}</li>
                                             </ul>
                                        </div>

                                   </div>
                              )
                         })
                    }
               </div>
          </div>
	)
     
     
}

export default FormationPage
