import { Component } from 'react';

class FormationPage extends Component{

     state ={
       formation : ""
     }
   
     getData = async() =>{
       const response = await fetch("/showformation");
       const body = await response.json();
       if (response.status !=200) throw Error(body.message);
       return body;
     }
     componentDidMount() {
       this.getData()
         .then(res=>{
           const data = res;
           this.setState({formation: data});
         })
   
     }
   
     render(){
          let foramtionArr= [];

       const {formation } = this.state;
          let formationP  = Array(formation);
          formationP = formationP[0]
          {for(let i in formationP){
               console.log();
               const formationChild = [formationP[i]['nom'],formationP[i]['duree'],formationP[i]['img'],formationP[i]['date_debut']];
               foramtionArr.push(formationChild);
          }}
       return (
		<div className="page formation">
               <h1>Nos formations</h1>
               <div className="grided formationGrid">
                    
                    {console.log(foramtionArr)}
                    
                    {
                         foramtionArr.map((formationItem)=>{
                              return(
                                   <div className="formationChild grided">
                                                                           {console.log(formationItem)}

                                        <div className="formationImg">

                                        </div>
                                        <div  className="formationInfo">
                                             <h2>{formationItem['0']}</h2>
                                             <ul className="formationDetails">
                                                  <li>Duree: {formationItem[1]}</li>
                                                  <li key={formationItem.date}>date de debut: {formationItem[3]}</li>
                                             </ul>
                                        </div>

                                   </div>
                              )
                         })
                    }
              
               </div>
          </div>
	     )
     }
   }
   

export default FormationPage

function SignUppage(){
	
	return (
		<div className="page signup authenticate">
               <div className="offset"></div>
               <div className="authenticate-form">
                    <form>
                         <span className="formText">
                              <h2>
                                   Inscription
                              </h2>
                              <p>
                                   Debutez votre voyage numerique <br></br>un monde plein d'adventure !
                              </p>

                         </span>
                         <span className="formInputs signup">
                              <input type="text" placeholder="Nom et prenom"/>
                              <input type="email" placeholder="email"/>
                              <span className="grided twoCol">
                                   <input type="date" placeholder="date de naissance"/>
                                   <select className="inputStyle">
                                        <option>Sexe:</option>
                                        <option>Homme</option>
                                        <option>Femme</option>
                                   </select>
                              </span>
                              <select className="inputStyle">
                                   <option>Choisir votre filiere:</option>
                                   {formation.map(formatChild=>{
                                        return(
                                             <option>{formatChild.nom}</option>
                                        )
                                        
                                   })}
                              </select>
                              <span className="grided twoCol">
                                   <select className="inputStyle">
                                        <option>Annee Inscription</option>
                                        <option>License 1</option>
                                        <option>License 2</option>
                                        <option>License 3</option>
                                        <option>Master I</option>
                                        <option>Master II</option>
                                   </select>
                                   <select className="inputStyle">
                                        <option>Situation Matrimonial</option>
                                        <option>Celibataire</option>
                                        <option>Marie</option>
                                        <option>Divorce</option>
                                        <option>C'est complique</option>
                                   </select>
                                   
                              </span>
                              
                              <label>
                                   <input className="rememberCheck" type="checkbox"/>
                                   &nbsp;Vous accepter tous nos termes d'utilisation

                              </label>

                         </span>
                         <span className="formSubmit">
                              <input type="submit" value="s'inscrire" className="button primBtn" />

                         </span>
                         
                    </form>
               </div>
               
          </div>
	)
}

function LoginPage(){
	
	return (
		<div className="page login authenticate">
               <div className="offset"></div>
               <div className="authenticate-form">
                    <form>
                         <span className="formText">
                              <h2>
                                   Connection
                              </h2>
                              <p>
                                   Connectez vous dans notre monde numerique <br/> et profitez de la magie!
                              </p>

                         </span>
                         <span className="formInputs login">
                              <input type="text" placeholder="Email or username"/>
                              <input type="password" placeholder="password"/>
                              <label>
                                   <input className="rememberCheck" type="checkbox"/>
                                   &nbsp;Se souvenir de moi?  

                              </label>

                         </span>
                         <span className="formSubmit">
                              <input type="submit" value="connection" className="button primBtn" />

                         </span>
                         
                    </form>
               </div>
               
          </div>
	)
}

export {LoginPage, SignUppage}
